package org.exoplatform.appCenter.services.rest;

import javax.ws.rs.core.Response;

import org.json.JSONObject;

import org.exoplatform.appCenter.services.entity.ApplicationForm;
import org.exoplatform.appCenter.services.entity.ApplicationImage;
import org.exoplatform.appCenter.services.test.AbstractServiceTest;
import org.exoplatform.services.security.ConversationState;
import org.exoplatform.services.security.Identity;

public class TestApplicationsRestService extends AbstractServiceTest {

  public void testAppManagement() throws Exception {

    ApplicationForm applicationForm = new ApplicationForm("app1",
                                                          "http://www.app1.com",
                                                          "app1",
                                                          true,
                                                          true,
                                                          new String[] {"*:/platform/users"},
                                                          "data:image/jpg;base64,",
                                                          "testImage");

    setConversationState();
    Response response = applicationsRestService.addApplication(mockUriInfo,
                                                                    applicationForm);
    assertNotNull(response);
    assertEquals(200, response.getStatus());

    applicationForm.setId(new Long(1));
    applicationForm.setDescription("app1 modif");

    response = applicationsRestService.editApplication(mockUriInfo,
                                                            applicationForm);
    assertNotNull(response);
    assertEquals(200, response.getStatus());

    response = applicationsRestService.getApplicationsList(mockUriInfo, "1", "2", "");

    JSONObject applicationsListJson = new JSONObject(response.getEntity()
                                                           .toString());
    assertNotNull(response);
    assertEquals(200, response.getStatus());
    assertEquals(1, applicationsListJson.getInt("totalApplications"));
    assertEquals(1, applicationsListJson.getInt("totalPages"));

    response = applicationsRestService.getAuthorizedApplicationsList(mockUriInfo, "1", "2", "");

    JSONObject authorizedApplicationsList = new JSONObject(response.getEntity()
                                                                 .toString());
    assertNotNull(response);
    assertEquals(200, response.getStatus());
    assertEquals(0, authorizedApplicationsList.getInt("totalApplications"));
    assertEquals(0, authorizedApplicationsList.getInt("totalPages"));
    
    response = applicationsRestService.addFavoriteApplication(mockUriInfo,
                                                                            new Long(1));
    assertNotNull(response);
    assertEquals(200, response.getStatus());
    
    response = applicationsRestService.getFavoriteApplicationsList(mockUriInfo);

    JSONObject favoriteApplicationsList = new JSONObject(response.getEntity()
                                                               .toString());
    assertNotNull(response);
    assertEquals(200, response.getStatus());
    assertEquals(1, favoriteApplicationsList.getJSONArray("applications").length());

    response = applicationsRestService.deleteFavoriteApplication(mockUriInfo,
                                                                      new Long(1));
    assertNotNull(response);
    assertEquals(200, response.getStatus());
    

    response = applicationsRestService.deleteApplication(mockUriInfo,
                                                              new Long(1));
    assertNotNull(response);
    assertEquals(200, response.getStatus());
  }

  public void testAppGeneralSettings() throws Exception {

    Response response = applicationsRestService.setMaxFavoriteApps(mockUriInfo,
                                                                        "100");

    assertNotNull(response);
    assertEquals(200, response.getStatus());

    setConversationState();
    ApplicationImage defaultAppImage = new ApplicationImage("data:image/jpg;base64,",
                                                            "testImage");

    response = applicationsRestService.setDefaultAppImage(mockUriInfo,
                                                               defaultAppImage);
    assertNotNull(response);
    assertEquals(200, response.getStatus());

    response = applicationsRestService.getAppGeneralSettings(mockUriInfo);

    assertNotNull(response);
    assertEquals(200, response.getStatus());
  }

  private void setConversationState() throws Exception {
    ConversationState state = new ConversationState(new Identity("john"));
    ConversationState.setCurrent(state);
  }
}
